esphome:
  name: jk-bms-hex-capture
  friendly_name: "JK-BMS HEX Data Capture"

esp32:
  board: esp32dev
  framework:
    type: arduino

# Enable comprehensive logging
logger:
  level: VERBOSE
  baud_rate: 0  # Disable serial logging to prevent interference

# Enable Home Assistant API
api:
  reboot_timeout: 0s

ota:
  - platform: esphome
    password: "D0m8st1c"

wifi:
  ssid: "Skylark"
  password: "arvensis"

  # Enable fallback hotspot
  ap:
    ssid: "JK-BMS-HEX Fallback"
    password: "fallbackpw"

captive_portal:

# UART configuration for RS485 JK-BMS communication
uart:
  - id: uart_jk
    baud_rate: 115200
    tx_pin: GPIO17
    rx_pin: GPIO16
    data_bits: 8
    parity: NONE
    stop_bits: 1
    debug:
      direction: BOTH
      dummy_receiver: false
      after:
        delimiter: "\n"
      sequence:
        - lambda: UARTDebug::log_string(direction, bytes);

# Global variables for data tracking
globals:
  - id: packet_count
    type: int
    initial_value: '0'

# Status sensors
sensor:
  - platform: uptime
    name: "JK-BMS HEX Capture Uptime"

  - platform: wifi_signal
    name: "JK-BMS HEX WiFi Signal"
    update_interval: 60s

  # Custom sensor for data packet count
  - platform: template
    name: "JK-BMS Packets Received"
    id: packets_received
    accuracy_decimals: 0
    unit_of_measurement: "packets"
    lambda: |-
      return id(packet_count);

binary_sensor:
  - platform: status
    name: "JK-BMS HEX Capture Status"

text_sensor:
  - platform: wifi_info
    ip_address:
      name: "JK-BMS HEX IP Address"
    ssid:
      name: "JK-BMS HEX WiFi SSID"

  # Latest raw HEX data
  - platform: template
    name: "JK-BMS Latest HEX Data"
    id: latest_hex_data
    lambda: |-
      return {"No data yet"};

# Raw UART data logging switches
switch:
  - platform: template
    name: "JK-BMS Raw Logging"
    id: raw_logging
    optimistic: true

  - platform: template
    name: "JK-BMS HEX Debug Mode"
    id: hex_debug_mode
    optimistic: true

# Manual data request button (disabled - testing passive listening)
# button:
#   - platform: template
#     name: "JK-BMS Request Data"
#     on_press:
#       - lambda: |-
#           uint8_t request[] = {0x4E, 0x57, 0x00, 0x13, 0x00, 0x00, 0x00, 0x00, 0x06, 0x03, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x68, 0x00, 0x00, 0x01, 0x29};
#           id(uart_jk).write_array(request, sizeof(request));
#           ESP_LOGI("jk_bms", "Manual data request sent");

# Interval component for periodic data requests and capture
interval:
  - interval: 1s
    then:
      - lambda: |-
          // Read any available UART data
          std::vector<uint8_t> data;
          while (id(uart_jk).available()) {
            uint8_t byte;
            if (id(uart_jk).read_byte(&byte)) {
              data.push_back(byte);
            }
          }

          if (!data.empty()) {
            // Convert to HEX string for logging
            std::string hex_str = "";
            for (size_t i = 0; i < data.size(); i++) {
              char hex_byte[4];
              sprintf(hex_byte, "%02X", data[i]);
              hex_str += hex_byte;
              if (i < data.size() - 1) hex_str += " ";
            }

            ESP_LOGI("jk_hex", "CAPTURED HEX DATA (%d bytes): %s", data.size(), hex_str.c_str());

            // JK-BMS Protocol Decoding
            if (data.size() >= 4 && data[0] == 0x4E && data[1] == 0x57) {
              ESP_LOGI("jk_bms", "=== JK-BMS FRAME DETECTED ===");

              // Extract frame length (bytes 2-3, big-endian)
              uint16_t frame_length = (data[2] << 8) | data[3];
              ESP_LOGI("jk_bms", "Frame length: %d bytes", frame_length);

              if (data.size() >= frame_length + 7) { // Header + data + checksum
                // Basic frame validation
                uint16_t calculated_checksum = 0;
                for (size_t i = 0; i < frame_length + 4; i++) {
                  calculated_checksum += data[i];
                }

                // Look for cell voltages in typical JK-BMS positions
                std::vector<float> cell_voltages;
                ESP_LOGI("jk_bms", "=== CELL VOLTAGE ANALYSIS ===");

                // Cell voltages typically start around byte 6 and are 2-byte big-endian values
                for (size_t i = 6; i < data.size() - 1 && cell_voltages.size() < 32; i += 2) {
                  uint16_t raw_voltage = (data[i] << 8) | data[i + 1];

                  // JK-BMS voltages are often in mV, check for LiFePO4 range
                  if (raw_voltage >= 2500 && raw_voltage <= 3650) {
                    float voltage = raw_voltage / 1000.0;
                    cell_voltages.push_back(voltage);
                    ESP_LOGI("jk_bms", "Cell %d: %.3fV (0x%04X) at offset %d", 
                            cell_voltages.size(), voltage, raw_voltage, i);
                  }

                  // Also check if voltage data is in different encoding (10ths of mV)
                  else if (raw_voltage >= 25000 && raw_voltage <= 36500) {
                    float voltage = raw_voltage / 10000.0;
                    cell_voltages.push_back(voltage);
                    ESP_LOGI("jk_bms", "Cell %d: %.4fV (0x%04X) at offset %d [10ths mV]", 
                            cell_voltages.size(), voltage, raw_voltage, i);
                  }
                }

                // Calculate cell differentials (delta) if we have valid cells
                if (cell_voltages.size() >= 2) {
                  float min_voltage = *std::min_element(cell_voltages.begin(), cell_voltages.end());
                  float max_voltage = *std::max_element(cell_voltages.begin(), cell_voltages.end());
                  float delta = max_voltage - min_voltage;

                  ESP_LOGI("jk_bms", "=== CELL DIFFERENTIAL ANALYSIS ===");
                  ESP_LOGI("jk_bms", "Cells found: %d", cell_voltages.size());
                  ESP_LOGI("jk_bms", "Min voltage: %.3fV", min_voltage);
                  ESP_LOGI("jk_bms", "Max voltage: %.3fV", max_voltage);
                  ESP_LOGI("jk_bms", "Delta (imbalance): %.3fV", delta);

                  if (delta > 0.100) {
                    ESP_LOGW("jk_bms", "HIGH CELL IMBALANCE: %.3fV - Balancing needed!", delta);
                  } else if (delta > 0.050) {
                    ESP_LOGI("jk_bms", "Moderate cell imbalance: %.3fV", delta);
                  } else {
                    ESP_LOGI("jk_bms", "Good cell balance: %.3fV", delta);
                  }
                }

                // Try to identify BMS unit (look for potential unit ID in header)
                if (data.size() >= 8) {
                  uint8_t potential_unit_id = data[4]; // Common position for unit ID
                  if (potential_unit_id <= 4) { // Reasonable range for 4 BMS system
                    ESP_LOGI("jk_bms", "Potential BMS Unit ID: %d", potential_unit_id);
                  }
                }

                ESP_LOGI("jk_bms", "=== END JK-BMS FRAME ===");
              } else {
                ESP_LOGW("jk_bms", "Incomplete JK-BMS frame: expected %d bytes, got %d", frame_length + 7, data.size());
              }
            }

            // Legacy pattern detection for comparison
            ESP_LOGD("jk_hex", "=== LEGACY PATTERN DETECTION ===");
            for (size_t i = 0; i < data.size() - 1; i++) {
              uint16_t value = (data[i] << 8) | data[i + 1];
              if (value >= 2500 && value <= 3650) {
                ESP_LOGD("jk_hex", "Legacy voltage pattern at offset %d: %.3fV", i, value / 1000.0);
              }
            }

            // Update packet counter
            id(packet_count) = id(packet_count) + 1;
          }

  # Removed periodic requests - testing if JK-BMS streams data naturally
  # - interval: 10s
  #   then:
  #     - lambda: |-
  #         uint8_t request[] = {0x4E, 0x57, 0x00, 0x13, 0x00, 0x00, 0x00, 0x00, 0x06, 0x03, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x68, 0x00, 0x00, 0x01, 0x29};
  #         id(uart_jk).write_array(request, sizeof(request));
  #         ESP_LOGI("jk_bms", "Periodic data request sent");

